var leftPin = ['P8_11', 'P8_12']
var rightPin = ['P8_13', 'P8_14']
var leftPWM = 'P9_14'
var rightPWM = 'P9_16'
var b = context.global.bone
var freq = 50;

try {

    var init = context.get('init') || false
    msg.payload.init = init
    if (!init) {
        // 初始化
        //b.pinMode(leftPWM, b.INPUT)
        b.pinMode(leftPWM, b.ANALOG_OUTPUT)
        //b.pinMode(rightPWM, b.INPUT)
        b.pinMode(rightPWM, b.ANALOG_OUTPUT)
        b.pinMode(leftPin[0], b.OUTPUT)
        b.pinMode(leftPin[1], b.OUTPUT)
        b.pinMode(rightPin[0], b.OUTPUT)
        b.pinMode(rightPin[1], b.OUTPUT)

        b.digitalWrite(leftPin[0], b.LOW)
        b.digitalWrite(leftPin[1], b.LOW)
        b.digitalWrite(rightPin[0], b.LOW)
        b.digitalWrite(rightPin[1], b.LOW)

        b.analogWrite(leftPWM, 0.0, freq)
        b.analogWrite(rightPWM, 0.0, freq)

        context.set('left', 0)
        context.set('right', 0)

        context.set('init', true)
    }

    var prevL = context.get('left') || 0
    var prevR = context.get('right') || 0
    var l = typeof(msg.payload.left) != 'undefined' ? parseFloat(msg.payload.left) : prevL
    var r = typeof(msg.payload.right) != 'undefined' ? parseFloat(msg.payload.right) : prevR
    msg.payload.left = l
    msg.payload.right = r

    if (l != prevL) {
        if (l > 0) {
            b.digitalWrite(leftPin[0], b.HIGH)
            b.digitalWrite(leftPin[1], b.LOW)
        } else if (l < 0) {
            b.digitalWrite(leftPin[0], b.LOW)
            b.digitalWrite(leftPin[1], b.HIGH)
        } else {
            console.log('Stopping Left')
            b.digitalWrite(leftPin[0], b.LOW)
            b.digitalWrite(leftPin[1], b.LOW)
        }
        b.analogWrite(leftPWM, b.abs(l), freq)
    }

    if (r != prevR) {
        if (r > 0) {
            b.digitalWrite(rightPin[0], b.HIGH)
            b.digitalWrite(rightPin[1], b.LOW)
        } else if (r < 0) {
            b.digitalWrite(rightPin[0], b.LOW)
            b.digitalWrite(rightPin[1], b.HIGH)
        } else {
            b.digitalWrite(rightPin[0], b.LOW)
            b.digitalWrite(rightPin[1], b.LOW)
        }
        b.analogWrite(rightPWM, b.abs(r), freq)
    }

    context.set('left', l)
    context.set('right', r)
    node.status({fill:"green",shape:"dot",text:l + ':' + r})
} catch(e) {
    console.log(e)
    msg.payload.error = e
}

return msg;

